        private static int ParseInput(string input)
        {
            int count = 0;
            int moves = 0;

            Console.WriteLine(input.Length);
            Console.ReadKey();

            for (var i = 0; i < input.Length; i++)
            {
                moves++;
                count = count + CheckFloorDirection(input[i]);
                if (IsInBasement(count))
                {
                    Console.WriteLine(moves);
                    Console.ReadKey();
                }
            }

            return count;
        }

        private static int CheckFloorDirection(char floorDirection)
        {
            if (floorDirection == '(')
            {
                return 1;
            }
            if (floorDirection == ')')
            {
                return -1;
            }
            return 0;
        }

        private static bool IsInBasement(int count)
        {
            if (count < 0)
            {
                return true;
            }

            return false;
        }
